// src/components/LoginDialog.tsx

import React, { useState } from 'react';
import Dialog from './Dialog';
import Button from './Button';
import { useNavigate } from 'react-router-dom';

interface LoginDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onLoginSuccess: () => void;
}

const LoginDialog: React.FC<LoginDialogProps> = ({ isOpen, onClose, onLoginSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('token', data.token);
        onLoginSuccess();
        onClose();
      } else {
        alert(data.error);
      }
    } catch (error) {
      console.error('Login error:', error);
      alert('An error occurred during login.');
    }
  };

  return (
    <Dialog isOpen={isOpen} onClose={onClose}>
      <h2 className="text-2xl mb-4">Log In</h2>
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="mb-4">
          <label>Email:</label>
          <input
            type="email"
            className="w-full mt-2 px-4 py-2 bg-gray-100 rounded-lg"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-4">
          <label>Password:</label>
          <input
            type="password"
            className="w-full mt-2 px-4 py-2 bg-gray-100 rounded-lg"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="flex justify-end">
          <Button text="Log In" className="bg-blue-500 text-white mr-2" onClick={handleLogin} />
          <Button
            text="Sign Up Now"
            className="bg-green-500 text-white"
            onClick={() => {
              onClose();
              navigate('/register'); // Assuming you have a registration page
            }}
          />
        </div>
      </form>
    </Dialog>
  );
};

export default LoginDialog;
