// src/pages/GalleryPage.tsx

import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import ImageDetailsDialog from '../components/ImageDetailsDialog';
import DeleteConfirmationDialog from '../components/DeleteConfirmationDialog';
import BottomNav from '../components/BottomNav';
import { useNavigate } from 'react-router-dom';

interface Image {
  _id: string;
  imageUrl: string;
  bodyPrompt: string;
  facePrompt: string;
  resolution: string;
  timestamp: string;
}

const GalleryPage: React.FC = () => {
  const [images, setImages] = useState<Image[]>([]);
  const [selectedImageDetails, setSelectedImageDetails] = useState<Image | null>(null);
  const [isDetailsDialogOpen, setIsDetailsDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [imageToDelete, setImageToDelete] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user's images
    const fetchImages = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await fetch('/api/images/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          console.log('Fetched images:', data.images); // Debugging
          setImages(data.images);
        } else {
          console.error('Failed to fetch images:', response.statusText);
          navigate('/login');
        }
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    fetchImages();
  }, [navigate]);

  const handleImageClick = (image: Image) => {
    setSelectedImageDetails(image);
    setIsDetailsDialogOpen(true);
  };

  const handleDeleteClick = (imageId: string) => {
    setImageToDelete(imageId);
    setIsDeleteDialogOpen(true);
  };

  const handleConfirmDelete = async () => {
    if (!imageToDelete) return;

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await fetch(`/api/images/${imageToDelete}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setImages(images.filter((img) => img._id !== imageToDelete));
        setIsDeleteDialogOpen(false);
        setImageToDelete(null);
        alert('Image deleted successfully!');
      } else {
        const data = await response.json();
        alert(`Failed to delete image: ${data.error || response.statusText}`);
      }
    } catch (error) {
      console.error('Error deleting image:', error);
      alert('An error occurred while deleting the image.');
    }
  };

  return (
    <>
      <main className="flex flex-col items-center px-7 pb-16 mx-auto w-full max-w-7xl text-white bg-red-500 min-h-screen">
        <Header title="My Gallery" />
        {images.length === 0 ? (
          <p className="mt-6">No images found. Start generating some!</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mt-6 w-full">
            {images.map((image) => (
              <div key={image._id} className="relative group">
                <img
                  src={image.imageUrl}
                  alt="Generated"
                  className="w-full rounded-lg cursor-pointer transform transition-transform duration-200 hover:scale-105"
                  onClick={() => handleImageClick(image)}
                />
                <button
                  className="absolute top-2 right-2 bg-red-600 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200"
                  onClick={() => handleDeleteClick(image._id)}
                >
                  âœ–
                </button>
              </div>
            ))}
          </div>
        )}

        {selectedImageDetails && (
          <ImageDetailsDialog
            isOpen={isDetailsDialogOpen}
            onClose={() => setIsDetailsDialogOpen(false)}
            imageDetails={{
              timestamp: selectedImageDetails.timestamp,
              resolution: selectedImageDetails.resolution,
              bodyPrompt: selectedImageDetails.bodyPrompt,
              facePrompt: selectedImageDetails.facePrompt,
            }}
          />
        )}

        <DeleteConfirmationDialog
          isOpen={isDeleteDialogOpen}
          onClose={() => setIsDeleteDialogOpen(false)}
          onConfirm={handleConfirmDelete}
        />
      </main>
      <BottomNav />
    </>
  );
};

export default GalleryPage;
