// src/pages/GalleryPage.tsx

import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import ImageDetailsDialog from '../components/ImageDetailsDialog';
import DeleteConfirmationDialog from '../components/DeleteConfirmationDialog';
import { useNavigate } from 'react-router-dom';

interface Image {
  _id: string;
  imageUrl: string;
  bodyPrompt: string;
  facePrompt: string;
  resolution: string;
  timestamp: string;
}

const GalleryPage: React.FC = () => {
  const [images, setImages] = useState<Image[]>([]);
  const [selectedImageDetails, setSelectedImageDetails] = useState<Image | null>(null);
  const [isDetailsDialogOpen, setIsDetailsDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [imageToDelete, setImageToDelete] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user's images
    const fetchImages = async () => {
      try {
        const response = await fetch('/api/images/user', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setImages(data.images);
        } else {
          navigate('/login');
        }
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    fetchImages();
  }, [navigate]);

  const handleImageClick = (image: Image) => {
    setSelectedImageDetails(image);
    setIsDetailsDialogOpen(true);
  };

  const handleDeleteClick = (imageId: string) => {
    setImageToDelete(imageId);
    setIsDeleteDialogOpen(true);
  };

  const handleConfirmDelete = async () => {
    try {
      const response = await fetch(`/api/images/${imageToDelete}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        setImages(images.filter((img) => img._id !== imageToDelete));
        setIsDeleteDialogOpen(false);
        setImageToDelete(null);
      } else {
        alert('Failed to delete image.');
      }
    } catch (error) {
      console.error('Error deleting image:', error);
      alert('An error occurred.');
    }
  };

  return (
    <main className="flex flex-col items-center px-4 py-6 bg-red-500 text-white min-h-screen">
      <Header title="My Gallery" />
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mt-6 w-full max-w-4xl">
        {images.map((image) => (
          <div key={image._id} className="relative">
            <img
              src={image.imageUrl}
              alt="Generated"
              className="w-full rounded-lg cursor-pointer"
              onClick={() => handleImageClick(image)}
            />
            <button
              className="absolute top-2 right-2 bg-red-600 text-white rounded-full p-1"
              onClick={() => handleDeleteClick(image._id)}
            >
              âœ–
            </button>
          </div>
        ))}
      </div>

      {selectedImageDetails && (
        <ImageDetailsDialog
          isOpen={isDetailsDialogOpen}
          onClose={() => setIsDetailsDialogOpen(false)}
          imageDetails={{
            timestamp: selectedImageDetails.timestamp,
            resolution: selectedImageDetails.resolution,
            bodyPrompt: selectedImageDetails.bodyPrompt,
            facePrompt: selectedImageDetails.facePrompt,
          }}
        />
      )}

      <DeleteConfirmationDialog
        isOpen={isDeleteDialogOpen}
        onClose={() => setIsDeleteDialogOpen(false)}
        onConfirm={handleConfirmDelete}
      />
    </main>
  );
};

export default GalleryPage;
