// src/pages/GalleryPage.tsx

import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import ImageDetailsDialog from '../components/ImageDetailsDialog';
import DeleteConfirmationDialog from '../components/DeleteConfirmationDialog';
import BottomNav from '../components/BottomNav';
import { useNavigate } from 'react-router-dom';

interface Image {
  _id: string;
  imageUrl: string;
  filename: string;
  bodyPrompt: string;
  facePrompt: string;
  resolution: string;
  timestamp: string;
}

// Mock data to simulate fetched images
const mockImages: Image[] = [
  // ... (As defined above with 9 images)
];

const GalleryPage: React.FC = () => {
  const [images, setImages] = useState<Image[]>([]);
  const [selectedImageDetails, setSelectedImageDetails] = useState<Image | null>(null);
  const [isDetailsDialogOpen, setIsDetailsDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [imageToDelete, setImageToDelete] = useState<string | null>(null);
  const navigate = useNavigate();

  // Define the BACKEND_AVAILABLE flag
  const BACKEND_AVAILABLE = false; // Set to true when backend is ready

  useEffect(() => {
    // Function to fetch images from the backend
    const fetchImages = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          console.warn('No token found, redirecting to login.');
          navigate('/login');
          return;
        }

        const response = await fetch('/api/images/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          console.log('Fetched images:', data.images); // Debugging
          if (Array.isArray(data.images)) {
            setImages(data.images);
          } else {
            console.error('Invalid data format for images:', data.images);
            // Fallback to mock data if the format is incorrect
            setImages(mockImages);
          }
        } else {
          console.error('Failed to fetch images:', response.status, response.statusText);
          // Fallback to mock data on failure
          setImages(mockImages);
        }
      } catch (error) {
        console.error('Error fetching images:', error);
        // Fallback to mock data on error
        setImages(mockImages);
      }
    };

    // Toggle between real fetch and mock data based on backend availability
    if (BACKEND_AVAILABLE) {
      fetchImages();
    } else {
      // Use mock data when backend is not available
      setImages(mockImages);
    }
  }, [navigate, BACKEND_AVAILABLE]);

  const handleImageClick = (image: Image) => {
    setSelectedImageDetails(image);
    setIsDetailsDialogOpen(true);
  };

  const handleDeleteClick = (imageId: string) => {
    setImageToDelete(imageId);
    setIsDeleteDialogOpen(true);
  };

  const handleConfirmDelete = async () => {
    if (!imageToDelete) return;

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.warn('No token found, redirecting to login.');
        navigate('/login');
        return;
      }

      const response = await fetch(`/api/images/${imageToDelete}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setImages(images.filter((img) => img._id !== imageToDelete));
        setIsDeleteDialogOpen(false);
        setImageToDelete(null);
        alert('Image deleted successfully!');
      } else {
        const data = await response.json();
        alert(`Failed to delete image: ${data.error || response.statusText}`);
      }
    } catch (error) {
      console.error('Error deleting image:', error);
      alert('An error occurred while deleting the image.');
    }
  };

  const handleMockDelete = () => {
    if (!imageToDelete) return;
    setImages(images.filter((img) => img._id !== imageToDelete));
    setIsDeleteDialogOpen(false);
    setImageToDelete(null);
    alert('Image deleted successfully (mock)!');
  };

  // Function to handle image download
  const handleDownload = (image: Image) => {
    const link = document.createElement('a');
    link.href = image.imageUrl;
    link.download = image.filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <>
      <main className="flex flex-col items-center px-7 pb-16 mx-auto w-full max-w-7xl text-white bg-red-500 min-h-screen pt-20">
        <Header title="Your Images:" />
        <div className="w-full mt-6 max-h-[70vh] overflow-y-auto">
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {images.map((image) => (
              <div
                key={image._id}
                className="relative group cursor-pointer"
                onClick={() => handleImageClick(image)}
              >
                <img
                  src={image.imageUrl}
                  alt={image.filename}
                  loading="lazy"
                  className="w-full h-auto rounded-lg object-cover transform transition-transform duration-200 hover:scale-105"
                />
                <button
                  className="absolute top-2 right-2 bg-red-600 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200"
                  onClick={(e) => {
                    e.stopPropagation(); // Prevent triggering the image click
                    handleDeleteClick(image._id);
                  }}
                  aria-label="Delete Image"
                >
                  ✖
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Image Overlay Modal */}
        {selectedImageDetails && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-75">
            <div className="bg-white rounded-lg overflow-hidden w-11/12 max-w-4xl">
              <div className="flex justify-end p-2">
                <button
                  onClick={() => setIsDetailsDialogOpen(false)}
                  className="text-gray-700 hover:text-gray-900"
                  aria-label="Close"
                >
                  ✖
                </button>
              </div>
              <div className="flex flex-col md:flex-row">
                <div className="md:w-1/2">
                  <img
                    src={selectedImageDetails.imageUrl}
                    alt={selectedImageDetails.filename}
                    className="w-full h-auto object-contain"
                  />
                </div>
                <div className="md:w-1/2 p-4">
                  <h2 className="text-2xl font-bold mb-2">{selectedImageDetails.filename}</h2>
                  <p className="mb-1">
                    <span className="font-semibold">Timestamp:</span>{' '}
                    {new Date(selectedImageDetails.timestamp).toLocaleString()}
                  </p>
                  <p className="mb-4">
                    <span className="font-semibold">Body Prompt:</span> {selectedImageDetails.bodyPrompt}
                  </p>
                  <p className="mb-4">
                    <span className="font-semibold">Face Prompt:</span> {selectedImageDetails.facePrompt}
                  </p>
                  <div className="flex space-x-4">
                    <button
                      onClick={() => handleDownload(selectedImageDetails)}
                      className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition duration-200"
                    >
                      Download
                    </button>
                    <button
                      onClick={() => {
                        handleDeleteClick(selectedImageDetails._id);
                        setIsDetailsDialogOpen(false);
                      }}
                      className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition duration-200"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Delete Confirmation Dialog */}
        <DeleteConfirmationDialog
          isOpen={isDeleteDialogOpen}
          onClose={() => setIsDeleteDialogOpen(false)}
          onConfirm={BACKEND_AVAILABLE ? handleConfirmDelete : handleMockDelete}
        />
      </main>
      <BottomNav />
    </>
  );
};

export default GalleryPage;
